import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { setContacts } from 'redux/actions';
import { AddContacts } from './Form/ContactsList';
import { Section } from './Section/Section';
import { SearchForm } from './SearchByName/SearchForm';
import { ContactsList } from './ContactsList/ContactsList';
import {
  PhonebookContext,
  usePhonebookContext,
} from '../context/PhonebookContext';
import PhonebookProvider from '../context/PhonebookContext';

export const App = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    if (
      JSON.parse(localStorage.getItem('contacts')) === undefined ||
      JSON.parse(localStorage.getItem('contacts')) === null
    ) {
      return;
    }
    const localUsers = JSON.parse(localStorage.getItem('contacts'));
    dispatch(setContacts(localUsers));
  });

  const { contacts } = usePhonebookContext();
  return (
    <div>
      <PhonebookProvider>
        <Section title="Phonebook">
          <AddContacts />
        </Section>
        <SearchForm />
        <ContactsList />
      </PhonebookProvider>
    </div>
  );
};

PhonebookContext.Provider.propTypes = {
  value: PropTypes.shape({
    contacts: PropTypes.array,
    filter: PropTypes.string,
    onFilterChange: PropTypes.func,
    onContactDelete: PropTypes.func,
    onFormSubmit: PropTypes.func,
  }),
};
