import { usePhonebookContext } from '../../context/PhonebookContext';
import { ContactListItem } from 'components/ContactListItem/ContactListItem';
import css from './ContactsList.module.css';

export const ContactsList = () => {
  const onContactDelete = id => {
    const deletedContacts = contacts.filter(contact => contact.id !== id);
    setContacts(deletedContacts);
    localStorage.setItem('contacts', JSON.stringify(deletedContacts));
  };

  const { contacts, filter} = usePhonebookContext();
  const filteredArray = contacts.filter(contact =>
    contact.name.toLowerCase().includes(filter.toLowerCase())
  );
  return (
    <ol className={css.list}>
      <h5>Contacts</h5>
      {filteredArray.map(({ id, name, number }) => (
        <ContactListItem
          key={id}
          contact={{ id, name, number }}
          onDelete={onContactDelete}
        />
      ))}
    </ol>
  );
};
