import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  filter: '',
};

const filterSlice = createSlice({
  name: 'filter',
  initialState: initialState.filter,
  reducers: {
    setFilters: {
      reducer: filter => {
        return filter;
      },
    },
  },
});

export const { setFilters } = filterSlice.actions;

export default filterSlice;
