export const getContacts = state => {
  if (state === undefined) {
    return;
  }
  if (state.filter) {
    return this.state.contacts.filter(contact => contact.name)
  }
  return state.contacts;
};

export const getFilter = state => {
  if (state === undefined) {
    return;
  }
  return state.filter;
};
